the_legend_of_zelda = Game.create({name: "The Legend of Zelda: Breath of the Wild", developer: "Nintendo EPD", genre: "Action-adventure", description: "The Legend of Zelda: Breath of the Wild is an action-adventure game developed and published by Nintendo. An entry in the longrunning The Legend of Zelda series, it was released for the Nintendo Switch and Wii U consoles on March 3, 2017. ", picture_url: "https://cdn02.nintendo-europe.com/media/images/10_share_images/games_15/wiiu_14/SI_WiiU_TheLegendOfZeldaBreathOfTheWild.jpg", release_date: "March 3, 2017", system: "Wii U", video: "https://www.youtube.com/embed/1rPxiXXxftE"})

mario_kart_8 = Game.create({name: "Mario Kart 8", developer: "Nintendo EAD", genre: "Kart racing", description: "Mario Kart 8 is a kart racing video game and the eighth major installment in the Mario Kart series, developed and published by Nintendo for their Wii U home video game console. The game was announced at E3 2013 and released worldwide in May 2014.", picture_url: "https://cdn02.nintendo-europe.com/media/images/10_share_images/games_15/wiiu_14/SI_WiiU_MarioKart8_image1600w.jpg" , release_date: "April 28, 2017", system: "Wii U", video:"https://www.youtube.com/embed/tKlRN2YpxRE"})

pikmin_3 = Game.create({name: "Pikmin 3", developer: "Nintendo EAD", genre: "Real-time strategy", description: "Pikmin 3 is a puzzle strategy video game developed and published by Nintendo for the Wii U video game console. It is the sequel to Pikmin 2 (2004) and was first released in Japan on July 13, 2013 and then in all other regions the following month. Game designer Shigeru Miyamoto first announced Pikmin 3 on July 16, 2008 as a game for the Wii console, later stating at E3 2011 that its development had transitioned to Wii U. The game retains the gameplay elements introduced in previous Pikmin games, and adds new features, including new playable characters and Pikmin types, Off-TV Play and downloadable content.", picture_url: "https://en.wikipedia.org/wiki/File:Pikmin_3_box_artwork.png" , release_date: "August 4, 2013", system: "Wii U", video: "https://www.youtube.com/embed/1rPxiXXxftE"})

new_super_mario_bros = Game.create({name: "New Super Mario Bros.U", developer: "Nintendo EAD", genre: "Platform", description: "The game's plot is similar to those of other side-scrolling Mario games. New Super Mario Bros. follows Mario as he fights his way through Bowser's henchmen to rescue Princess Peach. Mario has access to several power-ups that help him complete his quest, including the Super Mushroom, the Fire Flower, and the Starman, each giving him unique abilities. While traveling through eight worlds with a total of 80 levels, Mario must defeat Bowser Jr. and Bowser before finally saving Princess Peach.", picture_url: "https://en.wikipedia.org/wiki/File:New_Super_Mario_Bros._U_box_art.png" , release_date: "May 15, 2006", system: "Wii U", video: "https://www.youtube.com/embed/IQHa4qindfE"})

xenoblade_chronicles_x = Game.create({name: "Xenoblade Chronicles X", developer: "Monolith Soft", genre: "Action-role playing", description: "Xenoblade Chronicles X is an action role-playing video game developed by Monolith Soft and published by Nintendo for the Wii U home video game console in 2015. Xenoblade Chronicles X forms part of the Xeno metaseries, being a spiritual successor to Xenoblade Chronicles without any narrative connections to prior Xeno titles. Carrying over several gameplay elements from Xenoblade Chronicles, players explore the open world planet of Mira, completing a variety of quests and unlocking new regions to explore and gather resources from across Mira's five continents.", picture_url: "https://en.wikipedia.org/wiki/File:Xenoblade_Chronicles_X_-_Boxart.jpg" , release_date: "December 4, 2015", system: "Wii U", video: "https://www.youtube.com/embed/xksPVZptPGM"})

the_wonderful_101 = Game.create({name: "The Wonderful 101", developer: "Platinum Games", genre: "Action-adventure", description: "In The Wonderful 101, players control a horde of superheroes from an isometric viewpoint and can turn them into various objects called Unite Morphs. As levels progress, players must explore each stage to find helpless citizens and recruit them to join their army of heroes. The more heroes gathered, the greater the special morph powers can be. At the cost of depleting their battery meter, players can use "Unite Morph" forms to defeat enemies, solve puzzles, or traverse the environment.", picture_url: "https://en.wikipedia.org/wiki/File:Wonderful_101_box_artwork.jpg" , release_date: "September 15, 2013", system: "Wii U", video: "https://www.youtube.com/embed/z9ueBmNNGus"})

mario_party_10 = Game.create({name: "Mario Party 10", developer: "NDcube", genre: "Party", description: "Mario Party 10[a] is a party video game developed by NDcube and published by Nintendo for their Wii U home video game console. It is the tenth main installment in the Mario Party series, the fourteenth in the franchise, and the first and only game in the series for the Wii U. The game was released worldwide in March 2015. It is the first game in the series since Mario Party 4 to have Donkey Kong as a playable character for every aspect of the game.[b] Mario Party 10 was followed by Super Mario Party for the Nintendo Switch in October 2018.", picture_url: "https://en.wikipedia.org/wiki/File:Mario_Party_10_Small_Official_Boxart.png" , release_date: "March 20, 2015", system: "Wii U", video: "https://www.youtube.com/embed/L69Z39bgdU4"})

splatoon = Game.create({name: "Splatoon", developer: "Nintendo EAD", genre: "Third-person shooter", description: "Splatoon is primarily a team-based third-person shooter which is playable by up to eight players in online four-versus-four matches, although the game also features local one-versus-one multiplayer matches and a single player campaign. Players control characters called Inklings, who have the ability to shapeshift between human and squid forms. In humanoid form, Inklings can shoot ink in their team's color, which can be used to cover the environment or splat opposing players or enemies. Transforming into a squid allows players to swim through the ink of their own color, even up walls and through grates, hiding from the enemy and replenishing their ink supply in the process. Conversely, enemy ink is much slower to move across in humanoid form, cannot be swum through in squid form, and gradually deals damage. Players can use their ink to try and splat their opponents, which will send them back to their team's starting point whilst also providing a large splat of ink.", picture_url: "https://en.wikipedia.org/wiki/File:2717093-spratoonbox.jpg" , release_date: "May 29, 2015", system: "Wii U", video: "https://www.youtube.com/embed/8L54s2m1dPs"})

